import models from '../models'
import bcrypt from 'bcryptjs'
import token from '../service/token'

export default {
    register: async(req,res) => {
        try {

            console.log(req.body); // Verifica el cuerpo de la solicitud

            // ENCRIPTACIÓN DE CONTRASEÑA
            if (!req.body.password) {
                throw new Error('Password no proporcionado');
            }
            // ENCRIPTACIÓN DE CONTRASEÑA 12345678 -> fhjsdhf34j534jbj34bf34
            req.body.password = await bcrypt.hash(req.body.password,10);
            console.log(req.body.password);
            // const newUser = new models.User();
            // newUser.rol = req.body.role
            // newUser.name = req.body.nombre
            // newUser.surname = req.body.apellido
            // newUser.email = req.body.correo
            // newUser.password = req.body.contraseña
            // newUser.save();

            const User = await models.User.create(req.body);
            res.status(200).json({
                user: User,
            });
        } catch (error) {
            console.log(error);
            res.status(500).send({
                message: "OCURRIO UN PROBLEMA",
                error: error.message,
            });
        }
    },
    login: async(req,res) => {
        try {
            // email y contraseña
            // req.body.email y req.body.password
            const user = await models.User.findOne({
                email: req.body.email,
                state: 1,
            });
            if(user){
                // COMPARAR LAS CONTRASEÑA
                let compare = await bcrypt.compare(req.body.password,user.password);
                if(compare){
                    // UN USUARIO EXISTENTE Y ACTIVO
                    let tokenT = await token.encode(user._id,user.rol,user.email);

                    const USER_BODY = {
                        token: tokenT,
                        user: {
                            name: user.name,
                            surname: user.surname,
                            email: user.email,
                            // avatar: user.avatar 
                        }
                    }
                    res.status(200).json({
                        USER: USER_BODY,
                    });
                }else{
                    res.status(500).send({
                        message: 'EL USUARIO INGRESADO NO EXISTE',
                    });
                }
            }else{
                res.status(500).send({
                    message: 'EL USUARIO INGRESADO NO EXISTE',
                });
            }
        } catch (error) {
            console.log(error);
            res.status(500).send({
                message: 'HUBO UN ERROR',
            });
        }
    },
    update: async(req,res) => {
        try {
            // echo@gmail.com
            const VALID_USER = await models.User.findOne({email: req.body.email, _id: {$ne : req.body._id}});

            if(VALID_USER){
                res.status(200).json({
                    message: 403,
                    message_text: "EL USUARIO INGRESADO YA EXISTE",
                });
            }

            if(req.body.password){
                req.body.password = await bcrypt.hash(req.body.password,10);
            }
            const User = await models.User.findByIdAndUpdate({_id: req.body._id},req.body);

            //const NUser = await models.User.findById({_id: req.body._id});
            res.status(200).json({
                message: 'EL USUARIO SE EDITO CORRECTAMENTE',
                ///: resource.User.api_resource_user(NUser),
                user: User,
            });
        } catch (error) {
            console.log(error);
            res.status(500).send({
                message: 'OCURRIO UN PROBLEMA',
                error: error.message
            });
        }
    }, 

    list: async(req,res) => {
        try { 
            // localhost:3000?search=JOSE( JosE)
            var search = req.query.search;
            let USERS = await models.User.find({
                $or : [
                    {'name': new RegExp(search, 'i')},
                    {'surname': new RegExp(search, 'i')},
                    {'email': new RegExp(search, 'i')},
                ]
            }).sort({'createdAt': -1});
            res.status(200).json({
                users: USERS,
            });
           } catch (error) {
            console.log(error);
            res.status(500).send({
                message: 'OCURRIO UN PROBLEMA',
            });
        }
    },
    remove: async(req,res) => {
        try {
            let _id = req.params["id"];

            const User = await models.User.findByIdAndDelete({_id: _id});
            res.status(200).json({
                message: 'EL USUARIO SE ELIMINO CORRECTAMENTE',
            });
        } catch (error) {
            console.log(error);
            res.status(500).send({
                message: 'OCURRIO UN PROBLEMA'
            });
        }
    },

}

