                        });
                    }
                    return res.status(200).json({
                        appointment: appointment
                    });
                } else {
                    const appointments = await models.Appointment.findAll({
                        include: [
                            { model: models.Patient },
                            { model: models.Specialitie },
                            { model: models.User },
                            { model: models.DayHour,
                                include:[
                                    { 
                                      model: models.Day,
                                      attributes:['name']
                                    },
                                    {
                                     model: models.Hour,
                                     attributes:['name']
                                    }
                                 ]
                             }
                        ]
                    });
                    return res.status(200).json({
                        appointments: appointments,
