import models from '../models'
import bcrypt from 'bcryptjs'
import token from '../service/token'
import { Op } from 'sequelize';

export default {
    register: async (req, res) => {
        try {
            // ENCRIPTACIN DE CONTRASEA
            if (!req.body.password) {
                throw new Error('Password no proporcionado');
            }
            // ENCRIPTACIN DE CONTRASEA 12345678 -> fhjsdhf34j534jbj34bf34
            req.body.password = await bcrypt.hash(req.body.password, 10);
            console.log(req.body.password);

            const VALID_USER = await models.User.findOne({
                where: {
                    email: req.body.email,
                },
            });
            if (VALID_USER) {
                res.status(200).json({
                    message: 403,
                    message_text: "EL USUARIO INGRESADO YA EXISTE",
                });
            }
            const User = await models.User.create(req.body);
            res.status(200).json({
                user: User,
            });
        } catch (error) {
            console.log(error);
            res.status(500).send({
                message: "OCURRIO UN PROBLEMA",
                error: error.message,
            });
        }
    },
    login: async (req, res) => {
        try {
            // email y contrasea
            // req.body.email y req.body.password
            const user = await models.User.findOne({
                where: {
                    email: req.body.email,
                    state: 1
                }
            });
            if (user) {
                // COMPARAR LAS CONTRASEA
                let compare = await bcrypt.compare(req.body.password, user.password);
                if (compare) {
                    // UN USUARIO EXISTENTE Y ACTIVO
                    let tokenT = await token.encode(user.id, user.rol, user.email);

                    const USER_BODY = {
                        token: tokenT,
                        user: {
                            name: user.name,
                            surname: user.surname,
                            email: user.email,
                            id: user.id
                            // avatar: user.avatar 
                        }
                    }
                    res.status(200).json({
                        USER: USER_BODY,
                    });
                } else {
                    res.status(500).send({
                        message: 'EL USUARIO INGRESADO NO EXISTE',
                    });
                }
            } else {
                res.status(500).send({
                    message: 'EL USUARIO INGRESADO NO EXISTE',
                });
            }
        } catch (error) {
            console.log(error);
            res.status(500).send({
                message: 'HUBO UN ERROR',
            });
        }
    },
    update: async (req, res) => {
        try {
            // aba@gmail.com
            const userId = req.params.id;
            // Verificar si el email ya est en uso por otro usuario
            const VALID_USER = await models.User.findOne({
                where: {
                    email: req.body.email,
                    id: { [Op.ne]: userId }, // Asegrate de no encontrar al usuario actual
                },
            });
            if (VALID_USER) {
                res.status(200).json({
                    message: 403,
                    message_text: "EL USUARIO INGRESADO YA EXISTE",
                });
            }
            // Si se proporciona una nueva contrasea, hashearla
            if (req.body.password) {
                req.body.password = await bcrypt.hash(req.body.password, 10);
            }

            const [updated] = await models.User.update(req.body, {
                where: { id: userId },
            });

            if (updated) {
                // Si el usuario fue actualizado, obtener los nuevos datos
                const updatedUser = await models.User.findByPk(userId);

