import express from 'express';
import { checkRole, checkPermission } from '../middlewares/auth';
import { createUserWithRole } from '../service/userService';

const router = express.Router();

// Ruta para crear un usuario con un rol asignado
router.post('/users', async (req, res) => {
    try {
        const user = await createUserWithRole(req.body, 'Admin');
        res.json(user);
    } catch (error) {
        res.status(500).json({ message: error.message });
    }
});

// Ruta protegida que solo puede ser accedida por usuarios con el rol "Admin"
router.get('/admin-only', checkRole('Admin'), (req, res) => {
    res.json({ message: 'Acceso permitido a esta ruta solo para administradores' });
});

// Ruta protegida que solo puede ser accedida por usuarios con el permiso "view_reports"
router.get('/reports', checkPermission('view_reports'), (req, res) => {
    res.json({ message: 'Acceso permitido a la vista de reportes' });
});

export default router;

